import styled from "styled-components";

const colors = {
    primary:"salmon",
    secondary:"purple"
}
const Button = styled.button`
    padding:0.8rem 2rem;
    border:none;
    border-radius:10px;
    color:#fff;
    background-color:salmon;
    cursor:pointer;

    //menggunakan props variant
    background-color:${({variant}) => 
    colors[variant]|| colors["primary"]}
`;

export default Button;
