import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = () => {
  const [regions, setRegions] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://covid-fe-2023.vercel.app/api/indonesia.json');
      const regionsData = response.data.regions;
      setRegions(regionsData);
    } catch (error) {
      console.log('Error fetching data:', error);
    }
  };

  return (
    <div>
    <h1>Data</h1>
    {regions.length > 0 ? (
      <table>
        <thead>
          <tr>
            <th>Region</th>
            <th>Confirmed</th>
            <th>Recovered</th>
            <th>Treatment</th>
            <th>Deaths</th>
          </tr>
        </thead>
        <tbody>
          {regions.map((region, index) => (
            <tr key={index}>
              <td>{region.name}</td>
              <td>{region.numbers.confirmed}</td>
              <td>{region.numbers.recovered}</td>
              <td>{region.numbers.treatment}</td>
              <td>{region.numbers.death}</td>
            </tr>
          ))}
        </tbody>
      </table>
    ) : (
      <p>Loading...</p>
    )}
  </div>
  );
};

export default Home;
