/* eslint-disable eqeqeq */
/* eslint-disable no-unused-vars */
/* eslint-disable jsx-a11y/heading-has-content */
import StyledCovid from "./DetailGlobal.styled";

function DetailGlobal (props) {
    const {covid} = props;
    let totalColor;

    if (covid.numbers.confirmed) {
        totalColor = 'salmon';
    } else if (covid.numbers.recovered) {
        totalColor = '#118AB2';
    } else {
        totalColor = 'red';
    }
    return (
        <StyledCovid>
            <div>
            <h3>{covid.name}</h3><br></br>
            <p style={{ color: totalColor }}>Confirmed: {covid.numbers.confirmed}</p>
            <p style={{ color: totalColor }}>Recovered: {covid.numbers.recovered}</p>
            <p style={{ color: totalColor }}>Death: {covid.numbers.death}</p>
        </div>
        </StyledCovid>

    );
}

export default DetailGlobal;
