import {createSlice} from "@reduxjs/toolkit";
import data from "../../utils/constants/provinces";

const covidSlice = createSlice({
    name :"Covid slice",
    initialState : {
        covid: data.provinces
    },
    reducers:{
        updateCovid(state,action) {
            const {covid} = state;
            const {provinsi, status, jumlah} = action.payload;

            const index = covid.findIndex((item)=> item.kota === provinsi);

            covid[index][status] = parseInt(covid[index][status]) + parseInt(jumlah);


            }
        },
    },
)

//generate action and reducers
const {updateCovid} = covidSlice.actions;
const covidReducer = covidSlice.reducer;

export {updateCovid};
export default covidReducer;
